"use client"

import { useState, useEffect, useRef } from 'react';
import { useRouter } from 'next/navigation';
import Topbar from '@/components/Topbar';
import { FaPaperPlane } from 'react-icons/fa';
import { createClient } from '@/supabase';

const FAQChatbot = () => {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const chatContainerRef = useRef(null);
  const router = useRouter();
  const supabase = createClient();

  useEffect(() => {
    chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
  }, [messages]);

  const handleSendMessage = async () => {
    if (inputMessage.trim() === '') return;

    setIsLoading(true);
    setMessages(prev => [...prev, { type: 'user', content: inputMessage }]);
    setInputMessage('');

    try {
      const { data, error } = await supabase
        .from('faq_chatbot')
        .select('response')
        .eq('query', inputMessage)
        .single();

      if (error) throw error;

      const botResponse = data ? data.response : '申し訳ありませんが、その質問にはお答えできません。';
      setMessages(prev => [...prev, { type: 'bot', content: botResponse }]);
    } catch (error) {
      console.error('Error fetching response:', error);
      setMessages(prev => [...prev, { type: 'bot', content: 'エラーが発生しました。もう一度お試しください。' }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen h-full bg-gray-100 flex flex-col">
      <Topbar />
      <div className="flex-grow container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-6">FAQチャットボット</h1>
        <div className="bg-white rounded-lg shadow-md p-6">
          <div ref={chatContainerRef} className="h-96 overflow-y-auto mb-4">
            {messages.map((message, index) => (
              <div key={index} className={`mb-4 ${message.type === 'user' ? 'text-right' : 'text-left'}`}>
                <div className={`inline-block p-3 rounded-lg ${message.type === 'user' ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'}`}>
                  {message.content}
                </div>
              </div>
            ))}
            {isLoading && (
              <div className="text-center">
                <div className="inline-block p-3 bg-gray-200 rounded-lg">
                  回答を生成中...
                </div>
              </div>
            )}
          </div>
          <div className="flex">
            <input
              type="text"
              value={inputMessage}
              onChange={(e) => setInputMessage(e.target.value)}
              placeholder="質問を入力してください"
              className="flex-grow border border-gray-300 rounded-l-lg px-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            />
            <button
              onClick={handleSendMessage}
              disabled={isLoading}
              className="bg-blue-500 text-white px-4 py-2 rounded-r-lg hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
            >
              <FaPaperPlane />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FAQChatbot;

"use client";

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import Topbar from '@/components/Topbar';
import { createClientComponentClient } from '@supabase/auth-helpers-nextjs';
import { FaFileAlt, FaCalendarAlt, FaFileDownload } from 'react-icons/fa';
import axios from 'axios';

const CustomReport = () => {
  const router = useRouter();
  const supabase = createClientComponentClient();
  const [selectedItems, setSelectedItems] = useState([]);
  const [dateRange, setDateRange] = useState({ start: '', end: '' });
  const [reportFormat, setReportFormat] = useState('pdf');
  const [loading, setLoading] = useState(false);

  const reportItems = [
    { id: 1, name: '全体スコア' },
    { id: 2, name: 'カテゴリ別スコア' },
    { id: 3, name: '分析コメント' },
    { id: 4, name: '改善提案' },
    { id: 5, name: '進捗状況' },
  ];

  const handleItemSelect = (id) => {
    if (selectedItems.includes(id)) {
      setSelectedItems(selectedItems.filter(item => item !== id));
    } else {
      setSelectedItems([...selectedItems, id]);
    }
  };

  const handleDateChange = (e) => {
    setDateRange({ ...dateRange, [e.target.name]: e.target.value });
  };

  const handleFormatChange = (e) => {
    setReportFormat(e.target.value);
  };

  const generateReport = async () => {
    setLoading(true);
    try {
      const response = await axios.post('/api/generate-custom-report', {
        selectedItems,
        dateRange,
        reportFormat
      });

      // Handle the response, e.g., download the report
      console.log('レポート生成成功:', response.data);
      // TODO: レポートのダウンロード処理を実装
    } catch (error) {
      console.error('レポート生成エラー:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen h-full bg-gray-100">
      <Topbar />
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-8 text-gray-800">カスタムレポート作成</h1>

        <div className="bg-white shadow-md rounded-lg p-6 mb-8">
